import React from 'react';

class AboutUs extends React.Component {
    render() {
        return (
            <div>
                <div class="card text-center">
                    <div class="card-header">
                        <h4>About Us</h4>
                    </div>
                    <div class="card-body">
                     
                        <p class="card-text">The idea of forming an association for the welfare of community member was proposed by 
                        <strong>Shri S Raja Rao  , N G Vasudev Moorthy , S Ramakrishna ,Dr. K . Raghunath Rao, SriPrakash N, K Ramachandra Rao </strong> 
                        and  others  at a meeting in Kolar in early 2003. 
                        In  order to carry  forward this proposal, a meeting was convened in May 2003 on the auspicious day of “Akshaya Thritiya “ at Gangamma Kalyana Mantapa , Jayanagar 4th T Block , Bangalore, which was attended by about 150 members. It was in this meeting that the name of the sangha  ‘AKSHAYA VIPRA MAHA SABHA “ was proposed and accepted by the members unanimously.  
</p>
                        <a href="#" class="btn btn-primary">Go somewhere</a>
                    </div>
                    <div class="card-footer text-muted">
                        2 days ago
                    </div>
                </div>
            </div>
        )
    }

}

export default AboutUs;
